const html = $json.data;
if (!html) throw new Error("No HTML received from pricing page.");

const timestamp = new Date().toISOString();

// Remove scripts/styles but KEEP structure spacing
const text = html
  .replace(/<script[\s\S]*?<\/script>/gi, " ")
  .replace(/<style[\s\S]*?<\/style>/gi, " ")
  .replace(/<[^>]*>/g, " ")
  .replace(/\s+/g, " ");

// MODEL NAME PATTERN (only real OpenAI models)
const modelPattern = /\b(gpt-[0-9a-z\.\-]+|o[0-9]-mini|gpt-realtime[-a-z0-9]*|gpt-image-[0-9a-z\-]+)\b/gi;

// PRICE PATTERN
const pricePattern = /\$([\d.]+)/g;

const pricing = {};
let match;

while ((match = modelPattern.exec(text)) !== null) {
  const model = match[1].toLowerCase();

  // Look ahead in nearby text for two prices
  const snippet = text.substring(match.index, match.index + 200);

  const prices = [...snippet.matchAll(pricePattern)].map(p => parseFloat(p[1]));

  if (prices.length >= 2 && !pricing[model]) {
    pricing[model] = {
      input_price: prices[0],
      output_price: prices[2]
    };
  }
}

if (Object.keys(pricing).length < 3) {
  throw new Error("Pricing parse incomplete — pricing layout may have changed.");
}

return [{
  json: {
    pricing,
    pricing_fetch_timestamp: timestamp,
    payload:$('Webhook Trigger').first().json.body
  }
}];

// Get full input
const root = $input.first().json.data[0];

const pricing = $input.first().json.data[0].pricing;
const payload = $input.first().json.data[0].payload;

const promptText =$input.first().json.data[0].payload.prompt|| "";
const responseText = $input.first().json.data[0].payload.response || "";

// ---- Token estimation ----
// 1 token ≈ 4 characters
const estimateTokens = (text) => Math.ceil(text.length / 4);

const inputTokens = estimateTokens(promptText);
const outputTokens = estimateTokens(responseText);

// ---- Credit conversion ----
// 1 credit = $1 (change here if your system differs)
const USD_TO_CREDIT = 1;

// ---- Cost + Credit calculation ----
const results = [];

for (const modelName in pricing) {
  const modelPricing = pricing[modelName];

  const inputPrice = modelPricing.input_price || 0;
  const outputPrice = modelPricing.output_price || 0;

  // Cost (prices assumed per 1M tokens)
  const inputCost =
    (inputTokens / 1_000_000) * inputPrice;

  const outputCost =
    (outputTokens / 1_000_000) * outputPrice;

  const totalCost = inputCost + outputCost;

  // Credits
  const inputCredits = inputCost * USD_TO_CREDIT;
  const outputCredits = outputCost * USD_TO_CREDIT;
  const totalCredits = totalCost * USD_TO_CREDIT;

  results.push({
    model: modelName,

    input_tokens: inputTokens,
    output_tokens: outputTokens,

    input_cost_usd: Number(inputCost.toFixed(6)),
    output_cost_usd: Number(outputCost.toFixed(6)),
    total_cost_usd: Number(totalCost.toFixed(6)),

    input_credits: Number(inputCredits.toFixed(6)),
    output_credits: Number(outputCredits.toFixed(6)),
    total_credits: Number(totalCredits.toFixed(6)),

    pricing_timestamp: $input.first().json.data[0].pricing_fetch_timestamp,
    user_id: payload.user_id,
    prompt: promptText,
  });
}

// One row per model (Google Sheets friendly)
return results.map(row => ({ json: row }));
