// Get the transcript from the previous node
const transcript = $input.item.json.transcript;

// Regex to find timestamps like [00:00] or [00:00:00]
const timestampRegex = /\[\d{2}:\d{2}(:\d{2})?\]/g;

// Remove timestamps and clean extra spaces
const cleanedTranscript = transcript.replace(timestampRegex, '').replace(/\s+/g, ' ').trim();

return {
  json: {
    cleanedTranscript
  }
};

// 1. Get the text from the OpenAI node
const fullText = $input.first().json.text;

// 2. Split the text into lines
const lines = fullText.split('\n');

// 3. Filter the lines to find actual tasks 
// (Removing empty lines, table separators, and headers)
const actionItems = lines
  .map(line => line.trim())
  .filter(line => {
    return line.length > 0 && 
           !line.includes('---') && 
           !line.includes('| Item |') &&
           !line.startsWith('**');
  })
  .map(task => {
    // Clean up markdown bullets if the AI added them
    const cleanTask = task.replace(/^[•\-\*\d\.]+\s+/, '').replace(/\|/g, '').trim();
    return {
      json: {
        action_item: cleanTask
      }
    };
  });

return actionItems;
